var _Assets1ae98f10d3ac183c7640a24264c7f219a35c8e17 = "<!doctype html>\n<html lang=\"en\">\n  <head>\n    <title>电影</title>\n    <!-- Required meta tags -->\n    <meta charset=\"utf-8\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1, shrink-to-fit=no\">\n\n    <!-- Bootstrap CSS -->\n    <link rel=\"stylesheet\" href=\"https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css\" integrity=\"sha384-ggOyR0iXCbMQv3Xipma34MD+dH/1fQ784/j6cY/iJTQUOhcWr7x9JvoRxT2MZw1T\" crossorigin=\"anonymous\">\n  </head>\n\n  <style>\n    .movie{\n        width:250px;\n        height:370px;\n        float: left;\n        margin: 5px;\n    }\n    .movie img{\n        width:250px;\n        height:370px;\n    }\n    .movie .cover {\n        width:250px;\n        height:370px;\n        line-height: 20px;\n        background-color: grey;\n        translate: 80%;\n        position:relative;\n        top: -370px;\n        background-color: rgba(255,255,255,0.5);\n        text-align: center;\n        line-height: 370px;\n        font-weight: bold;\n        user-select:none;\n        display: none;\n    }\n\n    .mao {\n        background: inherit;\n        -webkit-filter: blur(5px);\n        -moz-filter: blur(5px);\n        -ms-filter: blur(5px);\n        -o-filter: blur(5px);\n        filter: blur(5px);\n        filter: progid:DXImageTransform.Microsoft.Blur(PixelRadius=9, MakeShadow=false);\n    }\n\n    .movie-content{\n        display:flex;\n        flex-wrap:wrap;\n        align-content:flex-start;\n        align-items: center;\n        justify-content:center;\n    }\n    .search_input{\n        width:500px;\n        margin: 0px auto;\n        margin-top: 30px;\n        margin-bottom: 10px;\n    }\n  </style>\n  <body>\n\n  \n\n    <div class=\"container\">\n        <input type=\"hidden\" id=\"hid_isload\" value=\"0\">\n        <input type=\"hidden\" id=\"last_id\" value=\"0\">\n        <div class=\"row\">\n            <input type=\"text\"\n                class=\"form-control search_input\" name=\"\" id=\"\" aria-describedby=\"helpId\" placeholder=\"输入要搜索的影片\">\n        </div>\n        <div class=\"row\">\n            <div class=\"col-xs-1-12 movie-content\">\n            </div>\n        </div>\n    </div>\n    <a href=\"/list/\"></a>\n      \n    <!-- Optional JavaScript -->\n    <!-- jQuery first, then Popper.js, then Bootstrap JS -->\n    <script src=\"http://code.jquery.com/jquery-2.1.1.min.js\"></script>\n    <script src=\"https://cdnjs.cloudflare.com/ajax/libs/popper.js/1.14.7/umd/popper.min.js\" integrity=\"sha384-UO2eT0CpHqdSJQ6hJty5KVphtPhzWj9WO1clHTMGa3JDZwrnQq4sF86dIHNDz0W1\" crossorigin=\"anonymous\"></script>\n    <script src=\"https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/js/bootstrap.min.js\" integrity=\"sha384-JjSmVgyd0p3pXB1rRibZUAYoIIy6OrQ6VrjIEaFf/nJGzIxFDsf4x0xIM+B07jRM\" crossorigin=\"anonymous\"></script>\n  </body>\n  <script>\n      $(function(){\n            $(document).on(\"mouseover\",\".movie\",function(env){\n                $(this).find(\".cover\").show()\n            })\n            $(document).on(\"mouseout\",\".movie\",function(env){\n                $(this).find(\".cover\").hide()\n            })\n            load()\n            $(document).on(\"click\",\".movie\",function(){\n                window.open($(this).attr(\"href\"), '_blank').location;\n            })\n      });\n      var jlheight = 50;             //距下边界长度/单位px\n      var totalheight = 0;\n      var is_load = 0;\n      var pages = 2;\n      var n_i = 0;\n      $(window).scroll(function () {\n          var srollPos = $(window).scrollTop();    //滚动条距顶部距离(页面超出窗口的高度)\n          totalheight = parseFloat($(window).height()) + parseFloat(srollPos);\n          is_load = $(\"#hid_isload\").val();\n          if (is_load == 0) {\n              if (($(document).height() - jlheight) <= totalheight) {\n                  $(\"#hid_isload\").attr(\"value\", \"1\");//滚动的时候设置成1：不执行滚动\n                  console.log(\"加载....\");\n                  load()\n              }\n          }\n      });\n\n      function load(){\n          lastOid = $(\".movie>div.cover\").last().attr(\"oid\")\n          if(lastOid == undefined) {\n              lastOid = \"\"\n          }\n          $.get(\"/list/\"+lastOid,function(content,status){\n              if (status != \"success\") {\n                  return \n              }\n              $(\"div.movie-content\").append(content)\n              $(\"#hid_isload\").attr(\"value\", \"0\")\n          })\n          console.log(lastOid)\n      }\n  </script>\n</html>"
var _Assets61a6c052b04233124da80ed6652a1cb70f38c9b8 = "<!doctype html>\n<html lang=\"en\">\n  <head>\n    <title>{{.data.Title}}</title>\n    <!-- Required meta tags -->\n    <meta charset=\"utf-8\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1, shrink-to-fit=no\">\n\n    <!-- Bootstrap CSS -->\n    <link rel=\"stylesheet\" href=\"https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css\" integrity=\"sha384-ggOyR0iXCbMQv3Xipma34MD+dH/1fQ784/j6cY/iJTQUOhcWr7x9JvoRxT2MZw1T\" crossorigin=\"anonymous\">\n    <link href=\"/video-js/video-js.css\" rel=\"stylesheet\">\n    <script src=\"/video-js/video.js\"></script>\n    <script src=\"/video-js/videojs-contrib-hls.min.js\"></script>\n    <script>\n        videojs.options.flash.swf = \"video-js/video-js.swf\"\n    </script>\n    <style>\n        #video-panel{\n            background-color: black;\n        }\n        #choose{\n            overflow-y:auto;\n            height: 100%;\n        }\n        #choose>img{\n            float: left;\n            height: 150px;\n        }\n        #choose>div{\n            word-break:break-all;\n            font-size: 0.7rem;\n        }\n        body::-webkit-scrollbar{\n                display: none; /* Chrome Safari */\n        }\n        body{\n            scrollbar-width: none; /* firefox */\n            -ms-overflow-style: none; /* IE 10+ */\n            overflow-x: hidden;\n            overflow-y: auto;\n        }\n        .my{\n            background-color: lightseagreen;\n            text-align: right;\n        }\n        .list-group-item{\n            padding: 3px;\n        }\n        .list-group-item > span{\n            color:rgb(243, 31, 12);\n        }\n        #talk>input{\n            position:relative;\n            height: 25px;\n            width: 300px;\n        }\n        \n    </style>\n  </head>\n  <body>\n    <div id=\"video-panel\" style=\"float:left\">\n        <video id=\"live-stream\" class=\"video-js vjs-default-skin vjs-big-play-centered\"\n        controls preload=\"auto\"\n        data-setup='{\"example_option\":true}'>\n        <source src=\"{{.default.url}}\" type=\"application/x-mpegURL\">\n        </video>\n    </div>\n    <ul class=\"nav nav-tabs\" id=\"my-tab\">\n        <li class=\"nav-item\">\n            <a href=\"#talk\" class=\"nav-link active\">聊天</a>\n        </li>\n        <li class=\"nav-item\">\n            <a href=\"#choose\" class=\"nav-link\">选集</a>\n        </li>\n    </ul>\n\n    <div class=\"tab-content\" id=\"my-tab-p\"> \n        <div class=\"tab-pane active\" id=\"talk\">\n            <ul class=\"list-group list-group-flush\">\n                <li class=\"list-group-item my\">  hello</li>\n                <li class=\"list-group-item other\"><span>lerko1:</span> hello my</li>\n            </ul>\n            <input type=\"text\" placeholder=\"输入并回车\">\n        </div>\n        <div class=\"tab-pane\" id=\"choose\">\n            <div>\n                <img src=\"{{.data.ImageURL}}\" alt=\"\" height=\"150px\">\n                <div>\n                    <div>标题： {{.data.Title}}</div>\n                    <div>产地： {{.data.Location}}</div>\n                    <div>类型： {{.data.Types}}</div>\n                    <div>演员： {{.data.Actor}}</div>\n                </div>\n            </div>\n            <div class=\"list-group\">\n                {{range .m3u3}}\n                <button type=\"button\" class=\"list-group-item list-group-item-action choose-button\" src=\"{{.url}}\">{{.title}}</button>\n                {{end}}\n            </div>\n        </div>\n    </div>\n      \n    <!-- Optional JavaScript -->\n    <!-- jQuery first, then Popper.js, then Bootstrap JS -->\n    <script src=\"https://code.jquery.com/jquery-3.3.1.slim.min.js\" integrity=\"sha384-q8i/X+965DzO0rT7abK41JStQIAqVgRVzpbzo5smXKp4YfRvH+8abtTE1Pi6jizo\" crossorigin=\"anonymous\"></script>\n    <script src=\"https://cdnjs.cloudflare.com/ajax/libs/popper.js/1.14.7/umd/popper.min.js\" integrity=\"sha384-UO2eT0CpHqdSJQ6hJty5KVphtPhzWj9WO1clHTMGa3JDZwrnQq4sF86dIHNDz0W1\" crossorigin=\"anonymous\"></script>\n    <script src=\"https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/js/bootstrap.min.js\" integrity=\"sha384-JjSmVgyd0p3pXB1rRibZUAYoIIy6OrQ6VrjIEaFf/nJGzIxFDsf4x0xIM+B07jRM\" crossorigin=\"anonymous\"></script>\n  </body>\n  <script>\n\n    player(window.innerWidth-300,window.innerHeight)\n    $(document).ready(function(){\n        $('#my-tab a').click(function (e) { \n            e.preventDefault();//阻止a链接的跳转行为 \n            $(this).tab('show');//显示当前选中的链接及关联的content \n        })\n        $(\".choose-button\").click(function(){\n            reset($(this).attr('src'))\n        });\n        $(\".tab-content\").height(window.innerHeight-$(\"#my-tab\").height()+\"px\")\n        $(\".list-group-flush\").height(window.innerHeight-27-$(\"#my-tab\").height()+\"px\");\n        $(\".list-group>button\").first().addClass(\"active\");\n        $(\".list-group>button\").click(function(){\n            $(\".list-group>button\").removeClass('active')\n            $(this).addClass('active')\n        })\n    })\n\n    var playerP\n    function player(width,height){\n        $(\"#video-panel\").width(width)\n        $(\"#video-panel\").height(height)\n        $(\"#live-stream\").width(width)\n        $(\"#live-stream\").height(height)\n        \n        console.log(arguments)\n        var options = {\n            width: width,\n            height: height\n        }\n        playerP = videojs('live-stream', options);\n    \n        playerP.on(['loadstart', 'play', 'playing', 'firstplay', 'pause', 'ended', 'adplay', 'adplaying', 'adfirstplay', 'adpause', 'adended', 'contentplay', 'contentplaying', 'contentfirstplay', 'contentpause', 'contentended', 'contentupdate'], function (e) {\n            console.warn('VIDEOJS player event: ', e.type);\n        });\n    }\n    function reset(src){\n        playerP.reset()\n        playerP.src([\n            {\n                type: 'application/x-mpegURL',\n                src: src\n            },\n        ])\n        playerP.load()\n        playerP.play()\n    }\n\n\n  </script>\n</html>"
