var _Assets61a6c052b04233124da80ed6652a1cb70f38c9b8 = "<!doctype html>\n<html lang=\"en\">\n  <head>\n    <title>{{.data.Title}}</title>\n    <!-- Required meta tags -->\n    <meta charset=\"utf-8\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1, shrink-to-fit=no\">\n\n    <!-- Bootstrap CSS -->\n    <link rel=\"stylesheet\" href=\"https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css\" integrity=\"sha384-ggOyR0iXCbMQv3Xipma34MD+dH/1fQ784/j6cY/iJTQUOhcWr7x9JvoRxT2MZw1T\" crossorigin=\"anonymous\">\n    <link href=\"/video-js/video-js.css\" rel=\"stylesheet\">\n    <script src=\"/video-js/video.js\"></script>\n    <script src=\"/video-js/videojs-contrib-hls.min.js\"></script>\n    <script>\n        videojs.options.flash.swf = \"video-js/video-js.swf\"\n    </script>\n    <style>\n        #video-panel{\n            background-color: black;\n        }\n        #choose{\n            overflow-y:auto;\n            height: 100%;\n        }\n        #choose>img{\n            margin:0px auto;\n            height: 150px;\n        }\n        #choose>div{\n            word-break:break-all;\n            font-size: 0.7rem;\n        }\n        body::-webkit-scrollbar{\n                display: none; /* Chrome Safari */\n        }\n        body{\n            scrollbar-width: none; /* firefox */\n            -ms-overflow-style: none; /* IE 10+ */\n            overflow-x: hidden;\n            overflow-y: auto;\n        }\n        .my{\n            background-color: lightseagreen;\n            text-align: right;\n        }\n        .list-group-item{\n            padding: 3px;\n        }\n        .list-group-item > span{\n            color:rgb(243, 31, 12);\n        }\n        #talk>input{\n            position:relative;\n            height: 25px;\n            width: 300px;\n        }\n\n        #comment_p{overflow-y: auto;}\n        #message_text{font-size: 0.55rem;}\n        .form-inline center{\n            margin:0px auto;\n            width:300px;\n        }\n        .form-group label{\n            width:120px;\n        }\n        .talk{\n            font-size:0.55rem;\n        }\n    </style>\n  </head>\n  <body>\n    <div id=\"video-panel\" style=\"float:left\">\n        <video id=\"live-stream\" class=\"video-js vjs-default-skin vjs-big-play-centered\"\n        controls preload=\"auto\"\n        data-setup='{\"example_option\":true}'>\n        <source src=\"{{.default.url}}\" type=\"application/x-mpegURL\">\n        </video>\n    </div>\n    <ul class=\"nav nav-tabs\" id=\"my-tab\">\n        <li class=\"nav-item\">\n            <a href=\"#choose\" class=\"nav-link active\">选集</a>\n        </li>\n        <li class=\"nav-item\">\n            <a href=\"#talk\" class=\"nav-link \">聊天</a>\n        </li>\n    </ul>\n\n    <div class=\"tab-content\" id=\"my-tab-p\"> \n        <div class=\"tab-pane\" id=\"talk\">\n            <span id=\"lastID\" style=\"display:none;\" data=\"\"></span>\n            <ul class=\"list-group list-group-flush\" id=\"comment_p\">\n            </ul>\n            <input type=\"text\" id=\"message_text\" placeholder=\"输入并回车\">\n        </div>\n        <div class=\"tab-pane active\" id=\"choose\">\n            <div>\n                <div style=\"display: flex;justify-content: center;align-items: center;\">\n                    <img src=\"{{.data.ImageURL}}\" alt=\"\" width=\"290px\">\n                </div>\n                <div>\n                    <div>标题： {{.data.Title}}</div>\n                    <div>产地： {{.data.Location}}</div>\n                    <div>类型： {{.data.Types}}</div>\n                    <div>演员： {{.data.Actor}}</div>\n                    <div>更新： {{.data.UpdateTime}}</div>\n                </div>\n            </div>\n            <div class=\"list-group\">\n                {{range .m3u3}}\n                <button type=\"button\" class=\"list-group-item list-group-item-action choose-button\" src=\"{{.url}}\">{{.title}}</button>\n                {{end}}\n            </div>\n        </div>\n    </div>\n\n    <!--登录模态框-->\n      <div class=\"modal fade\" id=\"login-mode\" tabindex=\"-1\" role=\"dialog\" aria-labelledby=\"login-mode\" aria-hidden=\"true\">\n        <div class=\"modal-dialog\">\n          <div class=\"modal-content\">\n            <div class=\"modal-header\">\n              <h5 class=\"modal-title\" id=\"login-mode\">注册</h5>\n              <button type=\"button\" class=\"close\" data-dismiss=\"modal\" aria-label=\"Close\">\n                <span aria-hidden=\"true\">&times;</span>\n              </button>\n            </div>\n            <div class=\"modal-body\">\n                <form class=\"form-inline center\">\n                    <div class=\"form-group \">\n                        <label for=\"\">用户名:</label>\n                        <input type=\"text\" name=\"username\" id=\"username\" class=\"form-control\" placeholder=\"\" aria-describedby=\"helpId\">\n                    </div>\n                    <div class=\"form-group \">\n                        <label for=\"\">密码:</label>\n                        <input type=\"password\" name=\"password\" id=\"password\" class=\"form-control\" placeholder=\"\" aria-describedby=\"helpId\">\n                    </div>\n                </form>\n            </div>\n            <div class=\"modal-footer\">\n              <button type=\"button\" class=\"btn btn-secondary\" data-dismiss=\"modal\">Close</button>\n              <button type=\"button\" id=\"login_action\" class=\"btn btn-primary\">登录</button>\n              <button type=\"button\" id=\"register_tigger\" class=\"btn btn-primary\">注册</button>\n            </div>\n          </div>\n        </div>\n      </div>\n\n    <!--注册模态框-->\n    <div class=\"modal fade\" id=\"register-mode\" tabindex=\"-1\" role=\"dialog\" aria-labelledby=\"register-mode\" aria-hidden=\"true\">\n        <div class=\"modal-dialog\">\n          <div class=\"modal-content\">\n            <div class=\"modal-header\">\n              <h5 class=\"modal-title\" id=\"register-mode\">注册</h5>\n              <button type=\"button\" class=\"close\" data-dismiss=\"modal\" aria-label=\"Close\">\n                <span aria-hidden=\"true\">&times;</span>\n              </button>\n            </div>\n            <div class=\"modal-body\">\n                <form class=\"form-inline center\">\n                    <div class=\"form-group \">\n                        <label for=\"\">用户名:</label>\n                        <input type=\"text\" name=\"username\" id=\"reg_username\" class=\"form-control\" placeholder=\"\" aria-describedby=\"helpId\">\n                    </div>\n                    <div class=\"form-group \">\n                        <label for=\"\">密码:</label>\n                        <input type=\"password\" name=\"password\" id=\"reg_password\" class=\"form-control\" placeholder=\"\" aria-describedby=\"helpId\">\n                    </div>\n                    <div class=\"form-group \">\n                        <label for=\"\">确认密码:</label>\n                        <input type=\"password\" name=\"password_re\" id=\"reg_password_re\" class=\"form-control\" placeholder=\"\" aria-describedby=\"helpId\">\n                    </div>\n                </form>\n            </div>\n            <div class=\"modal-footer\">\n              <button type=\"button\" class=\"btn btn-secondary\" data-dismiss=\"modal\">Close</button>\n              <button type=\"button\" id=\"register_login\" class=\"btn btn-primary\">注册</button>\n            </div>\n          </div>\n        </div>\n    </div>\n      \n    <!-- Optional JavaScript -->\n    <!-- jQuery first, then Popper.js, then Bootstrap JS -->\n    <script src=\"http://libs.baidu.com/jquery/2.0.0/jquery.min.js\"></script>\n    <script src=\"https://cdnjs.cloudflare.com/ajax/libs/popper.js/1.14.7/umd/popper.min.js\" integrity=\"sha384-UO2eT0CpHqdSJQ6hJty5KVphtPhzWj9WO1clHTMGa3JDZwrnQq4sF86dIHNDz0W1\" crossorigin=\"anonymous\"></script>\n    <script src=\"https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/js/bootstrap.min.js\" integrity=\"sha384-JjSmVgyd0p3pXB1rRibZUAYoIIy6OrQ6VrjIEaFf/nJGzIxFDsf4x0xIM+B07jRM\" crossorigin=\"anonymous\"></script>\n  </body>\n  <script>\n\n    player(window.innerWidth-300,window.innerHeight)\n    $(document).ready(function(){\n        $('#my-tab a').click(function (e) { \n            e.preventDefault();//阻止a链接的跳转行为 \n            $(this).tab('show');//显示当前选中的链接及关联的content \n        })\n        $(\".choose-button\").click(function(){\n            reset($(this).attr('src'))\n        });\n        $(\".tab-content\").height(window.innerHeight-$(\"#my-tab\").height()+\"px\")\n        $(\".list-group-flush\").height(window.innerHeight-27-$(\"#my-tab\").height()+\"px\");\n        $(\".list-group>button\").first().addClass(\"active\");\n        $(\".list-group>button\").click(function(){\n            $(\".list-group>button\").removeClass('active')\n            $(this).addClass('active')\n        })\n\n        //进来先加载两次\n        loadComment()\n\n        $(\"#comment_p\").scroll(function(){\n            top = $(\"#comment_p\").scrollTop()\n            if(top == 0){\n                loadComment()\n            }\n        })\n\n        $(\"#register_tigger\").click(function(){\n            $(\"#register-mode\").modal()\n        })\n        $(\"#login_action\").click(function(){\n            login()\n        })\n        $(\"#register_login\").click(function(){\n            register()\n        })\n        $('#message_text').bind('keypress',function(event){\n            if(event.keyCode == \"13\")    \n            {\n                token = localStorage.getItem(\"token\")\n                if(token == null || token == \"\"){\n                    $(\"#login-mode\").modal()\n                    return\n                }\n                talk($('#message_text').val())\n                $('#message_text').val(\"\")\n            }\n        });\n        token = localStorage.getItem(\"token\")\n        if(token != null && token != \"\"){\n            recv()\n        }\n    })\n\n    function loadComment(){\n        if($(\"#lastID\").attr('data') == \"\"){\n            url = \"http://localhost:8084/talk\"\n        }else{\n            url = \"http://localhost:8084/talk/\"+$(\"#lastID\").attr('data')\n        }\n        $.get(url,function(data){\n            append = \"\"\n            for (i=0;i<data.data.length;i++) {\n                if(i==0){\n                    $(\"#lastID\").attr('data',data.data[i].ID)\n                }\n                messageItem = data.data[i]\n                append += '<li class=\"list-group-item talk\"><span ID=\"'+messageItem.ID+'\">'+messageItem.Username+\"</span>: \"+messageItem.Content+'</li>'\n            }\n            $(\"#comment_p\").prepend(append)\n        })\n    }\n\n    function login(){\n        username = $(\"#username\").val()\n        password = $(\"#password\").val()\n        $.post(\"/login\",{\n            username:username,\n            password:password,\n        },function(data){\n            if(data.code == 200){\n                localStorage.setItem(\"token\",data.data.token)\n                alert(\"登录成功\")\n                $(\"#login-mode\").modal(\"hide\")\n                window.location.reload()\n            }else{\n                alert(\"登录失败\")\n            }\n        })\n    }\n\n    function register(){\n        username = $(\"#reg_username\").val()\n        password = $(\"#reg_password\").val()\n        passwordRe = $(\"#reg_password_re\").val()\n\n        if(password != passwordRe){\n            alert(\"两次密码输入不同\")\n            return\n        }\n\n        $.post(\"/register\",{\n            username:username,\n            password:password,\n            password_re:passwordRe\n        },function(data){\n            if(data.code == 200 && data.status){\n                alert(\"注册成功\")\n                $(\"#register-mode\").modal(\"hide\")\n                return\n            }else{\n                alert(\"注册失败\")\n                return\n            }\n        })\n    }\n\n    function player(width,height){\n        $(\"#video-panel\").width(width)\n        $(\"#video-panel\").height(height)\n        $(\"#live-stream\").width(width)\n        $(\"#live-stream\").height(height)\n        \n        console.log(arguments)\n        var options = {\n            width: width,\n            height: height\n        }\n        playerP = videojs('live-stream', options);\n    \n        playerP.on(['loadstart', 'play', 'playing', 'firstplay', 'pause', 'ended', 'adplay', 'adplaying', 'adfirstplay', 'adpause', 'adended', 'contentplay', 'contentplaying', 'contentfirstplay', 'contentpause', 'contentended', 'contentupdate'], function (e) {\n            console.warn('VIDEOJS player event: ', e.type);\n        });\n    }\n    function reset(src){\n        playerP.reset()\n        playerP.src([\n            {\n                type: 'application/x-mpegURL',\n                src: src\n            },\n        ])\n        playerP.load()\n        playerP.play()\n    }\n    function talk(msg){\n        if(msg == \"\"){\n            return\n        }\n        $.ajax({\n            type:\"POST\",\n            url:\"http://localhost:8084/room/{{.data.Hash}}\",\n            data:{\n                message:msg\n            },\n            headers:{\n                Authorization:localStorage.getItem(\"token\")\n            },\n            success:function(data){\n                console.log(data)\n            } \n        })\n    }\n\n    function recv(){\n        if (!!window.EventSource) {\n            var source = new EventSource('http://localhost:8084/stream/{{.data.Hash}}?token='+localStorage.getItem(\"token\"));\n            source.addEventListener('message', function(e) {\n                console.log(e.data)\n                eventComment = JSON.parse(e.data)\n                append = '<li class=\"list-group-item talk\">'+eventComment.data.message+'</li>'\n                $(\"#comment_p\").append(append)\n                $(\"#comment_p\").scrollTop($(\"#comment_p\").height()+100)\n            }, false);\n        } else {\n            alert(\"NOT SUPPORTED\");\n        }\n    }\n\n  </script>\n</html>"
